<?phpnamespace Bafl\Payment\Setup;use Magento\Eav\Setup\EavSetupFactory;use Magento\Framework\Setup\InstallDataInterface;use Magento\Framework\Setup\ModuleContextInterface;use Magento\Framework\Setup\ModuleDataSetupInterface;use Magento\Sales\Setup\SalesSetupFactory;class InstallData implements InstallDataInterface{    /**     * @var Magento\Sales\Setup\SalesSetupFactory     */    protected $salesSetupFactory;    /**     *     * @param \Magento\Sales\Setup\SalesSetupFactory $salesSetupFactory     *     */    public function __construct(SalesSetupFactory $salesSetupFactory)    {        $this->salesSetupFactory = $salesSetupFactory;    }    /**     * {@inheritdoc}     */    public function install(ModuleDataSetupInterface $setup, ModuleContextInterface $context)    {        /**         * Prepare database for install         */        $setup->startSetup();        $data = [];        $statuses = [			'baflpayment_failed' => __('BAFL Handshake Failed'),            'baflc_failed' => __('CARD Failure'),			'baflw_failed' => __('WALLET Failure'),            'bafla_failed' => __('Account Failure'),        ];        foreach ($statuses as $code => $info) {            $data[] = ['status' => $code, 'label' => $info];        }        $setup->getConnection()            ->insertArray($setup->getTable('sales_order_status'), ['status', 'label'], $data);        /****** sales_order_status_state *******/        $data1 = [];        $statusesState = [                'baflpayment_failed' => __('baflpayment_failed'),                'baflc_failed' => __('baflc_failed'),                'baflw_failed'  => __('baflw_failed'),                'bafla_failed' => __('bafla_failed'),        ];        foreach ($statusesState as $code => $info) {        	$data1[] = ['status' => $code, 'state' => $info  ,'is_default' => 1 , 'visible_on_front'=> 1];        }        $setup->getConnection()        ->insertArray($setup->getTable('sales_order_status_state'), ['status', 'state','is_default' ,'visible_on_front' ], $data1);        /**         * Prepare database after install         */        $setup->endSetup();    }}